delete(value) {
  // null
  if (!this.head) {
    return null;
  }

  // null
  let deletedNode = null;
  let currentNode = this.head;

  // 
  while (currentNode) {
    // 
    if (this.compare.equal(currentNode.value, value)) {
      deletedNode = currentNode;

      // 
      if (deletedNode === this.head) {
        // 
        this.head = deletedNode.next;

        // null
        if (this.head) {
          this.head.previous = null;
        }

        // null
