    // 
    if (charIndex >= word.length) {
      return;
    }

    // 
    const character = word[charIndex];
    // 
    const nextNode = currentNode.getChild(character);

    // Trie
    if (nextNode == null) {
      return;
    }

    // 
    depthFirstDelete(nextNode, charIndex + 1);

    // isCompleteWordfalse
    if (charIndex === (word.length - 1)) {
