```js
export default class BinarySearchTreeNode extends BinaryTreeNode {
  /**
   * @param {*} [value] - node value.
   * @param {function} [compareFunction] - comparator function for node values.
   */
  //  BinarySearchTreeNode 
  //  value  compareFunction 
  constructor(value = null, compareFunction = undefined) {
    //  value  BinaryTreeNode 
    super(value);

    // This comparator is used to compare node values with each other.
    //  compareFunction  compareFunction 
    this.compareFunction = compareFunction;

    //  Comparator  nodeValueComparator 
    //  compareFunction  Comparator 
    this.nodeValueComparator = new Comparator(compareFunction);
  }

  /**
   * 
   * @param {*} value - 
   * @return {BinarySearchTreeNode} - 
   */
  insert(value) {
    // 
    if (this.nodeValueComparator.equal(this.value, null)) {
      this.value = value;

      return this;
    }

    // 
    if (this.nodeValueComparator.lessThan(value, this.value)) {
      // 
      if (this.left) {
        return this.left.insert(value);
      }

      // 
      const newNode = new BinarySearchTreeNode(value, this.compareFunction);
      this.setLeft(newNode);

      return newNode;
    }

    // 
    if (this.nodeValueComparator.greaterThan(value, this.value)) {
      // 
      if (this.right) {
        return this.right.insert(value);
      }

      // 
      const newNode = new BinarySearchTreeNode(value, this.compareFunction);
      this.setRight(newNode);

      return newNode;
    }

    // 
    return this;
  }

  /**
   * 
   * @param {*} value - 
   * @return {BinarySearchTreeNode} - null
   */
  find(value) {
    // 
    if (this.nodeValueComparator.equal(this.value, value)) {
      return this;
    }

    // 
    if (this.nodeValueComparator.lessThan(value, this.value) && this.left) {
      // Check left nodes.
      return this.left.find(value);
    }

    // 
    if (this.nodeValueComparator.greaterThan(value, this.value) && this.right) {
      // Check right nodes.
      return this.right.find(value);
    }

    // 
    return null;
  }

  /**
   * @param {*} value
   * @return {boolean}
   */
  contains(value) {
    return !!this.find(value);
  }

  /**
   * 
   * @param {*} value - 
   * @return {boolean} - truefalse
   */
  remove(value) {
    // 
    const nodeToRemove = this.find(value);

    // 
    if (!nodeToRemove) {
      throw new Error("Item not found in the tree");
    }
    // 
    const { parent } = nodeToRemove;
    // 
    if (!nodeToRemove.left && !nodeToRemove.right) {
      // 
      if (parent) {
        parent.removeChild(nodeToRemove);
      } else {
        // undefined
        nodeToRemove.setValue(undefined);
      }
    } else if (nodeToRemove.left && nodeToRemove.right) {
      // 
      // 
      const nextBiggerNode = nodeToRemove.right.findMin();
      // 
      // 
      if (!this.nodeComparator.equal(nextBiggerNode, nodeToRemove.right)) {
        this.remove(nextBiggerNode.value);
        nodeToRemove.setValue(nextBiggerNode.value);
      } else {
        // 
